-- ----------------------------
DROP TABLE IF EXISTS `diseasecure`;
CREATE TABLE `diseasecure` (
  `did` int(11) NOT NULL AUTO_INCREMENT COMMENT '疾病诊断标准id',
  `dtypeid` int(255) NOT NULL COMMENT '类型id',
  `threeleveltitle` varchar(30) NOT NULL,
  `dcontent` varchar(3000) NOT NULL COMMENT '粗略内容',
  `remark2` varchar(255) DEFAULT NULL,
