-- ----------------------------
-- Table structure for `mediciedetails`
-- ----------------------------
DROP TABLE IF EXISTS `mediciedetails`;
CREATE TABLE `mediciedetails` (
  `mid` int(11) NOT NULL COMMENT '中成药id',
  `mcname` varchar(15) NOT NULL COMMENT '中文名',
  `mpname` varchar(50) NOT NULL COMMENT '拼音',
  `mformulation` varchar(20) DEFAULT NULL COMMENT '剂型（允许为空）',
  `mingredient` varchar(200) DEFAULT NULL COMMENT '成份',
  `mtraits` varchar(50) DEFAULT NULL COMMENT '性状',
  `mindications` varchar(400) NOT NULL COMMENT '适应症',
  `mspecification` varchar(30) DEFAULT NULL COMMENT '规格',
  `mdosage` varchar(120) DEFAULT NULL COMMENT '用法用量',
  `mnegativereactions` varchar(500) DEFAULT '尚不明确' COMMENT '不良反应',
  `mban` varchar(500) DEFAULT '尚不明确' COMMENT '禁忌',
  `mnotice` varchar(1000) NOT NULL DEFAULT '尚不明确' COMMENT '注意事项',
  `minteraction` varchar(1000) DEFAULT '如与其他药物同时使用可能会发生药物相互作用，详情请咨询医师或药师。' COMMENT '相互作用',
  `mstorage` varchar(20) DEFAULT NULL COMMENT '储藏',
  `mvalidity` varchar(10) DEFAULT NULL COMMENT '有效期',
  `mpackage` varchar(35) DEFAULT NULL COMMENT '包装',
  `mpicture` varchar(100) DEFAULT NULL COMMENT '图片地址',
  `mtypeid` int(11) DEFAULT NULL COMMENT '类型',
  `approvalnumber` varchar(50) DEFAULT NULL COMMENT '国药准字号',
  `manufacturer` varchar(50) DEFAULT NULL COMMENT '生产商',
  PRIMARY KEY (`mid`),
  KEY `fk_typeid` (`mtypeid`),
  CONSTRAINT `fk_typeid` FOREIGN KEY (`mtypeid`) REFERENCES `type` (`mtypeid`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of mediciedetails
-- ----------------------------

-- ----------------------------
-- Table structure for `record`
-- ----------------------------
DROP TABLE IF EXISTS `record`;
CREATE TABLE `record` (
  `mid` int(11) NOT NULL COMMENT '中成药详细表id',
  `rtime` datetime DEFAULT NULL COMMENT '查询时间',
  `rreference` int(11) DEFAULT NULL COMMENT '权重',
  `remark1` varchar(255) DEFAULT NULL COMMENT '备选1',
  `remark2` varchar(255) DEFAULT NULL COMMENT '备选2',
  `rid` int(11) NOT NULL COMMENT '记录id（与mid共同构成主键）',
  PRIMARY KEY (`mid`,`rid`),
  CONSTRAINT `rmid` FOREIGN KEY (`mid`) REFERENCES `mediciedetails` (`mid`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

